clc;
close all;
clear all
 load 'PIFSnet_LSF_Trout_TrainedModel'
dir_path = '';
file_name = sprintf('Acc_Dataset1_XIN_SLS.txt');
out = fullfile(dir_path,file_name);
fileID = fopen(out,'w');

fprintf(fileID,'\r\n');
 
Dri='';     
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
for ff=1:300
     Names = sprintf('%d%s',ff,'.png');
    
      Folder = fullfile(Dri,Names);
         GT=imread(Folder);  
         [x1,y1,z1]=size(GT);
         if z1==3
         GT=rgb2gray(GT);
         end
 
    num=num2str(ff); 
    file=strcat(num,'.jpg'); 
    image = (imread(file));
    im=image;
    im3=imresize(im,size(GT),'bilinear');
        [x1,x2,x3]=size(image); 
        Scolor1=image;
         Scolor2=image;
   if x3==3
   image=rgb2gray(image);
   NEW_img=image;
   end
      Orig_image=image;    
      imu=image>200;
       imd=image<150;

    tic;
       C = semanticseg(im,net);
      
toc;

 k2=C=='FISH';
 k3=C=='BG';

mask2=double(k2); 
mask3=double(k3); 

[labels_R1,scores_R1,allscores_R1] = semanticseg(im,net,'ExecutionEnvironment','gpu');

        xx= allscores_R1(:,:,1);
        newarr=zeros(size(xx));
        [lim1,lim2]=size(xx);
        for l1=1:lim1
            for l2=1:lim2
                
                if xx(l1,l2)<0.94
                    newarr(l1,l2)=1;
                end
                
            end
        end
         mask2(newarr==1)=0;
         mask2 = imresize(mask2,size(GT),'bilinear');
         mask2=bwareaopen(mask2, 1000);

         GT_OD=(GT~=0);
         
         imR=im3(:,:,1);
         imG=im3(:,:,2);
         imB=im3(:,:,3);
         [x1,y1]=size(GT_OD);
         
          tp_OD=0;
          tn_OD=0;
          fp_OD=0;
          fn_OD=0;
          
          
          tp_OC=0;
          tn_OC=0;
          fp_OC=0;
          fn_OC=0;
          
          

   for jj=1:x1 
        for kk=1:y1
        
            if  GT_OD(jj,kk)==1 && mask2(jj,kk)==1
                 imR(jj,kk)=0;
                 imG(jj,kk)=0;
                 imB(jj,kk)=255;
                    tp_OD=tp_OD+1;
           elseif GT_OD(jj,kk)==0 && mask2(jj,kk)==0
                   tn_OD=tn_OD+1;
            elseif GT_OD(jj,kk)==0 && mask2(jj,kk)==1  
                  imR(jj,kk)=0;
                  imG(jj,kk)=255;
                  imB(jj,kk)=0;
                  fp_OD=fp_OD+1;

             elseif GT_OD(jj,kk)==1 && mask2(jj,kk)==0
                 imR(jj,kk)=255;
                 imG(jj,kk)=0;
                 imB(jj,kk)=0;
                 
                 fn_OD=fn_OD+1;
             end
            
                   fp_OC=fp_OC+1;
  elseif GT_BG(jj,kk)==0 && mask3(jj,kk)==1  
      
        end
   end
   
mIoU_OD=(tp_OD/(tp_OD+fn_OD+fp_OD)*100);

  im1=cat(3,imR,imG,imB);
  
ImgStoreName = sprintf('%dR.tif',ff);
   imwrite(im1,ImgStoreName)

  fprintf(fileID,'%s\t%s\t%s\t%s\t%s\t%s\r\n',num2str(mIoU_OD),num2str(mIoU_OD),num2str(mIoU_OD),num2str(mIoU_OD),num2str(mIoU_OD),num2str(mIoU_OD));

end



